import styled from "styled-components";

export const Container = styled.div`
  color: red;
  text-align: center;
`;

export const FormContainer = styled.div`
  display: flex;
  margin-top: 30px;
  justify-content: center center;

  input {
    padding: 15px;
    background-color: #00fa9a;
    display: flex;
    border-radius: 5px;
    flex-wrap: wrap;
    height: 20px;
    left: 200px;
    justify-content: center;
`;
