import styled from "styled-components";

export const Container = styled.div`
  color: red;
  text-align: center;
`;

export const FormContainer = styled.div`
  display: flex;
  margin-top: 30px;
  justify-content: center;
  flex-direction: column;
  width: 40%;
  padding: 10px;
  margin: auto;

  button{ 
      padding: 20px;
      left: 100px;
  }  

  input {
    padding: 15px;
    margin: 10px;
    background-color: #00fa9a;
    display: flex;
    border-radius: 5px;
    flex-wrap: wrap;
    justify-content: center;
`;
