import styled from "styled-components";

export const Container = styled.div`
  color: red;
  text-align: center;

  input {
    padding: 15px;
    background-color: #00fa9a;
    display: flex;
    height: auto;
    left: 400px;
    align-items: left;
    justify-content: center;
    border-radius: 5px;
  }
`;
